import React, { useState } from 'react';

function VideoPreviewComponent({ videoURL }) {
  // Renders an image with the processed frame
  // Fetch the image from the backend
  // Use the videoURL prop to construct the URL
  // Use the correct HTTP method
  // Use the correct URL
  // Use the correct property from the response

  const [processedFramePath, setProcessedFramePath] = useState(null);

  const fetchProcessedFrameImage = async () => {
    try {
      console.log("videoURL", videoURL);
      const response = await fetch(
        `http://localhost:5000/processed_videos/${videoURL}`,
        {
          method: "GET",
        }
      );

      if (response.ok) {
        const processedFrameURL = URL.createObjectURL(await response.blob());
        setProcessedFramePath(processedFrameURL);
      } else {
        console.error("Failed to fetch processed frame image.");
      }
    } catch (error) {
      console.error("Error fetching processed frame image:", error);
    }
  }

  return (
    <div className="video-preview-component">
      <h2>Video Preview</h2>
      <img src={fetchProcessedFrameImage} alt="Processed frame" />
    </div>
  );
}

export default VideoPreviewComponent;
