// RegionSelector.js
import React, { useState } from "react";
import ImageDisplay from "./ImageDisplay";
import { useNavigate } from "react-router-dom";
import { useParams } from "react-router-dom";

const RegionSelector = ({ firstFrameUrl, handleGridSelection }) => {
  const navigate = useNavigate();

  const { videoId } = useParams();

  const [mode, setMode] = useState("stack"); // Default mode is 'stack'
  const [selectedSquares, setSelectedSquares] = useState([]);

  const handleModeChange = (newMode) => {
    setSelectedSquares([]);
    setMode(newMode);
  };

  const clearSelectedSquares = () => {
    setSelectedSquares([]);
  };

  const handleSquareClick = (squareIndex) => {
    if (selectedSquares.includes(squareIndex)) {
      setSelectedSquares(
        selectedSquares.filter((index) => index !== squareIndex)
      );
    } else {
      setSelectedSquares([...selectedSquares, squareIndex]);
    }
  };

  const handleSubmit = () => {
    if (selectedSquares.length > 0) {
      handleGridSelection(selectedSquares);
      navigate(`/data/${videoId}`);
    } else {
      alert("Please select at least one square.");
    }
  };

  return (
    <div className="region-selector">
      <h3>Select Regions</h3>
      <div className="mode-buttons">
        <button
          className={`mode-button ${mode === "stack" ? "active" : ""}`}
          onClick={() => handleModeChange("stack")}
        >
          Select Stack
        </button>
        <button
          className={`mode-button ${mode === "holeCards" ? "active" : ""}`}
          onClick={() => handleModeChange("holeCards")}
        >
          Select Hole Cards
        </button>
      </div>
      <ImageDisplay
        firstFrameUrl={firstFrameUrl}
        selectedSquares={selectedSquares}
        onSquareClick={handleSquareClick}
        mode={mode}
      />
      <button
        className="clear-button"
        disabled={selectedSquares.length === 0}
        onClick={clearSelectedSquares}
      >
        Clear
      </button>
      <br />
      <button
        className="submit-button"
        disabled={selectedSquares.length === 0}
        onClick={handleSubmit}
      >
        Submit
      </button>
    </div>
  );
};

export default RegionSelector;
