import React, { useState, useEffect } from "react";
import VideoUploadComponent from "./components/VideoUploadComponent";
import VideoPreviewComponent from "./components/VideoPreviewComponent";
import GridSelectionComponent from "./components/GridSelectionComponent";
import ColorSelectionComponent from "./components/ColorSelectionComponent";
import SubmitButton from "./components/SubmitButton";
import ProcessingFeedbackComponent from "./components/ProcessingFeedbackComponent";
import DataDisplayComponent from "./components/DataDisplayComponent";
import MultipleSelectionSupport from "./components/MultipleSelectionSupport";

function App() {
  const [videoURL, setVideoURL] = useState(null);
  const [selectedCells, setSelectedCells] = useState([]);
  const [selectedColor, setSelectedColor] = useState("red");
  const [processing, setProcessing] = useState(false);
  const [processedData, setProcessedData] = useState(null);
  const [multipleSelection, setMultipleSelection] = useState(false);
  const [processedFramePath, setProcessedFramePath] = useState(null);

  // Fetch processed frame image when videoURL changes
  useEffect(() => {
    if (videoURL) {
      fetchProcessedFrameImage();
    }
  }, [videoURL]);

  // Fetch processed frame image from the backend
  const fetchProcessedFrameImage = async () => {
    try {
      const response = await fetch(
        `http://localhost:5000/${videoURL}`,
        {
          method: "GET",
        }
      );
      console.log(response);

      if (response.ok) {
        const processedFrameURL = URL.createObjectURL(await response.blob());
        setProcessedFramePath(processedFrameURL);
      } else {
        console.error("Failed to fetch processed frame image.");
      }
    } catch (error) {
      console.error("Error fetching processed frame image:", error);
    }
  };

  const handleVideoUpload = async (videoURL) => {
    setVideoURL(videoURL);
  };

  const handleCellClick = (cell) => {
    if (multipleSelection) {
      if (selectedCells.includes(cell)) {
        setSelectedCells(selectedCells.filter((c) => c !== cell));
      } else {
        setSelectedCells([...selectedCells, cell]);
      }
    } else {
      setSelectedCells([cell]);
    }
  };

  const handleSelectColor = (color) => {
    setSelectedColor(color);
  };

  const handleProcessClick = () => {
    setProcessing(true);
    setProcessedData(null);

    fetch("http://localhost:5000/process", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        videoURL,
        selectedCells,
        selectedColor,
      }),
    })
      .then((response) => response.json())
      .then((data) => {
        setProcessing(false);
        setProcessedData(data);

        // Only set processedFramePath if processing is successful
        if (data.success) {
          setProcessedFramePath(data.processed_frame_path);
        }
      });
  };

  const handleToggleMultipleSelection = () => {
    setMultipleSelection(!multipleSelection);
  };

  return (
    <div className="App">
      <h1>Video Processing App</h1>

      {/* Video upload component */}
      <VideoUploadComponent onVideoUpload={handleVideoUpload} />

      {/* Video preview component */}
      <VideoPreviewComponent
        videoURL={videoURL}
        processedFramePath={processedFramePath}
      />
    </div>
  );
}

export default App;
