// src/components/ImageDisplay.js

import React from 'react';
import { sendGridSelection } from '../utils/mockApiUtils';
import './ImageDisplay.css';

const ImageDisplay = ({ firstFrameUrl, selectedQuadrant, onQuadrantClick }) => {
  const quadrantStyles = [
    { top: 0, left: 0 },
    { top: 0, right: 0 },
    { bottom: 0, left: 0 },
    { bottom: 0, right: 0 },
  ];

  const handleQuadrantClick = (quadrantIndex) => {
    onQuadrantClick(quadrantIndex);
  };

  const handleSubmit = async () => {
    // Call the mock API function to send the selected quadrants
    const response = await sendGridSelection(selectedQuadrant);

    if (response.success) {
      // Handle success (e.g., show a success message)
    }
  };

  return (
    <div className="image-display">
      {quadrantStyles.map((style, index) => (
        <div
          key={index}
          className={`quadrant ${selectedQuadrant === index ? 'selected' : ''}`}
          style={style}
          onClick={() => handleQuadrantClick(index)}
        />
      ))}
      <img
        src={firstFrameUrl}
        alt="First Frame"
        style={{ maxHeight: '100%', maxWidth: '100%' }}
      />
      <button onClick={handleSubmit}>Submit</button>
    </div>
  );
};

export default ImageDisplay;
