// RegionSelector.js
import React, { useState } from "react";
import ImageDisplay from "./ImageDisplay";
import { useNavigate } from "react-router-dom";
import { useParams } from "react-router-dom";

const RegionSelector = ({ firstFrameUrl, handleGridSelection }) => {
  const navigate = useNavigate();

  const { videoId } = useParams();

  const [selectedSquaresStack, setSelectedSquaresStack] = useState([]);
  const [selectedSquaresHoleCards, setSelectedSquaresHoleCards] = useState([]);
  const [currentMode, setCurrentMode] = useState("Stack"); // Initial mode is "Stack"


  const handleModeChange = (newMode) => {
    setMode(newMode);
  };

  const clearSelectedSquares = () => {
    setSelectedSquares([]);
  };

  const handleSquareClick = (squareIndex) => {
    if (currentMode === "Stack") {
      if (selectedSquaresStack.includes(squareIndex)) {
        setSelectedSquaresStack(selectedSquaresStack.filter(index => index !== squareIndex));
      } else {
        setSelectedSquaresStack([...selectedSquaresStack, squareIndex]);
      }
    } else if (currentMode === "HoleCards") {
      if (selectedSquaresHoleCards.includes(squareIndex)) {
        setSelectedSquaresHoleCards(selectedSquaresHoleCards.filter(index => index !== squareIndex));
      } else {
        setSelectedSquaresHoleCards([...selectedSquaresHoleCards, squareIndex]);
      }
    }
  };

  const handleSubmit = () => {
    if (mode === 'stack') {
      if (selectedSquaresStack.length > 0) {
        handleGridSelection(selectedSquaresStack, mode);
        // navigate to the appropriate page
      } else {
        alert('Please select at least one square.');
      }
    } else if (mode === 'holeCards') {
      if (selectedSquaresHoleCards.length > 0) {
        handleGridSelection(selectedSquaresHoleCards, mode);
        // navigate to the appropriate page
      } else {
        alert('Please select at least one square.');
      }
    }
  };

  return (
    <div className="region-selector">
      <h3>Select Regions</h3>
      <div className="mode-buttons">
        <button
          className={`mode-button ${mode === "stack" ? "active" : ""}`}
          onClick={() => handleModeChange("stack")}
        >
          Select Stack
        </button>
        <button
          className={`mode-button ${mode === "holeCards" ? "active" : ""}`}
          onClick={() => handleModeChange("holeCards")}
        >
          Select Hole Cards
        </button>
      </div>
      <ImageDisplay
        firstFrameUrl={firstFrameUrl}
        selectedSquares={mode === 'stack' ? selectedSquaresStack : selectedSquaresHoleCards}
        onSquareClick={handleSquareClick}
        mode={mode}
      />
      <button
        className="clear-button"
        disabled={selectedSquares.length === 0}
        onClick={clearSelectedSquares}
      >
        Clear
      </button>
      <br />
      <button
        className="submit-button"
        disabled={selectedSquares.length === 0}
        onClick={handleSubmit}
      >
        Submit
      </button>
    </div>
  );
};

export default RegionSelector;
