// RegionSelector.js
import React, { useState } from "react";
import ImageDisplay from "./ImageDisplay";
import { useNavigate } from "react-router-dom";
import { useParams } from "react-router-dom";

const RegionSelector = ({ firstFrameUrl, handleGridSelection }) => {
  const navigate = useNavigate();

  const { videoId } = useParams();

  const [selectedSquaresStack, setSelectedSquaresStack] = useState([]);
  const [selectedSquaresHoleCards, setSelectedSquaresHoleCards] = useState([]);
  const [currentMode, setCurrentMode] = useState("Stack"); // Initial mode is "Stack"

  const handleSquareClick = (squareIndex) => {
    if (currentMode === "Stack") {
      if (selectedSquaresStack.includes(squareIndex)) {
        setSelectedSquaresStack(selectedSquaresStack.filter(index => index !== squareIndex));
      } else {
        setSelectedSquaresStack([...selectedSquaresStack, squareIndex]);
      }
    } else if (currentMode === "HoleCards") {
      if (selectedSquaresHoleCards.includes(squareIndex)) {
        setSelectedSquaresHoleCards(selectedSquaresHoleCards.filter(index => index !== squareIndex));
      } else {
        setSelectedSquaresHoleCards([...selectedSquaresHoleCards, squareIndex]);
      }
    }
  };

  const handleClear = () => {
    if (currentMode === "Stack") {
      setSelectedSquaresStack([]);
    } else if (currentMode === "HoleCards") {
      setSelectedSquaresHoleCards([]);
    }
  };

  const handleSubmit = () => {
    let selectedSquares;
    if (currentMode === "Stack") {
      selectedSquares = selectedSquaresStack;
    } else if (currentMode === "HoleCards") {
      selectedSquares = selectedSquaresHoleCards;
    }

    if (selectedSquares.length > 0) {
      handleGridSelection(selectedSquares, currentMode); // Pass mode to the handler
      navigate(`/data/${videoId}`);
    } else {
      alert('Please select at least one square.');
    }
  };

  const handleModeChange = (mode) => {
    setCurrentMode(mode);
  };

  return (
    <div className="region-selector">
      <h3>Select Regions</h3>
      <div className="prompt">
        {currentMode === "Stack" ? "Select Stack" : "Select Hole Cards"}
      </div>
      <button onClick={() => handleModeChange("Stack")}>Select Stack</button>
      <button onClick={() => handleModeChange("HoleCards")}>Select Hole Cards</button>
      <button onClick={handleClear}>Clear</button>
      <ImageDisplay
        firstFrameUrl={firstFrameUrl}
        selectedSquares={
          currentMode === "Stack" ? selectedSquaresStack : selectedSquaresHoleCards
        }
        onSquareClick={handleSquareClick}
        currentMode={currentMode}
      />
      <button
        className="submit-button"
        disabled={
          (currentMode === "Stack" && selectedSquaresStack.length === 0) ||
          (currentMode === "HoleCards" && selectedSquaresHoleCards.length === 0)
        }
        onClick={handleSubmit}
      >
        Submit
      </button>
    </div>
  );
};

export default RegionSelector;
