import React from 'react';
import './ImageDisplay.css';

const ImageDisplay = ({ firstFrameUrl, selectedSquares, onSquareClick }) => {
  const numRows = 30;
  const numCols = 30;
  const squareSize = 100 / numRows; // Assuming each square is 1% of the image size

  const handleSquareClick = (row, col) => {
    const squareIndex = row * numCols + col;
    onSquareClick(squareIndex);
  };

  return (
    <div className="image-display">
      <div className="grid-container">
        {Array.from({ length: numRows }).map((_, rowIndex) => (
          <div key={rowIndex} className="grid-row">
            {Array.from({ length: numCols }).map((_, colIndex) => (
              <div
                key={colIndex}
                className={`grid-square ${selectedSquares.includes(
                  rowIndex * numCols + colIndex
                ) ? 'selected' : ''}`}
                style={{
                  width: `${squareSize}%`,
                  paddingTop: `${squareSize}%`,
                }}
                onClick={() => handleSquareClick(rowIndex, colIndex)}
              />
            ))}
          </div>
        ))}
      </div>
      <img
        src={firstFrameUrl}
        alt="First Frame"
        style={{ maxHeight: '100%', maxWidth: '100%' }}
      />
    </div>
  );
};

export default ImageDisplay;
